import { fighter } from './Fighter';

/**
 * Clase que representa un combate entre dos luchadores
 * @class Combat
 * @attribute {Fighter} fighter1
 * @attribute {Fighter} fighter2
 */
export class Combat {
  private fighter1: fighter;
  private fighter2: fighter;

  /**
   * Constructor
   * @param fighter1 {Fighter} Luchador 1
   * @param fighter2 {Fighter} Luchador 2
   * @constructor
   */
  constructor(fighter1: fighter, fighter2: fighter) {
    this.fighter1 = fighter1;
    this.fighter2 = fighter2;
  }

  /**
   * Método que devuelve al luchador 1
   * @returns {Fighter} Luchador 1
   */
  public getFighter1(): fighter {
    return this.fighter1;
  }

  /**
   * Método que devuelve al luchador 2
   * @returns {Fighter} Luchador 2
   */
  public getFighter2(): fighter {
    return this.fighter2;
  }

  /**
   * Método que simula el combate entre dos luchadores y devuelve el ganador y una
   * cadena con la historia del combate
   * @returns [Fighter, string] Ganador y historia del combate
   */
  public fight(): [fighter, string] {
    let output: string = '';
    const damage1: number = this.fighter1.getDamage(this.fighter2);
    const damage2: number = this.fighter2.getDamage(this.fighter1);
    let hp1: number = this.fighter1.getHP();
    let hp2: number = this.fighter2.getHP();
    output += `--- ${this.fighter1.getName()} vs ${this.fighter2.getName()} ---\n`;
    output += `${this.fighter1.getName()} HP: ${hp1}\n`;
    output += `${this.fighter2.getName()} HP: ${hp2}\n`;
    output += `----------------------------------------------------------------\n`;
    while (true) {
      output += `--- Turno de ${this.fighter1.getName()} ---\n`;
      hp2 -= damage1;
      output += `${this.fighter1.getCatchingPhrase()}\n`;
      if (hp2 <= 0) {
        output += `${this.fighter1.getName()} ha ganado la pelea\n`;
        return [this.fighter1, output];
      }
      output += `${this.fighter1.getName()} HP: ${hp1}\n`;
      output += `${this.fighter2.getName()} HP: ${hp2}\n`;
      output += `----------------------------------------------------------------\n`;
      output += `--- Turno de ${this.fighter2.getName()} ---\n`;
      hp1 -= damage2;
      output += `${this.fighter2.getCatchingPhrase()}\n`;
      if (hp1 <= 0) {
        output += `${this.fighter2.getName()} ha ganado la pelea\n`;
        return [this.fighter2, output];
      }
      output += `${this.fighter1.getName()} HP: ${hp1}\n`;
      output += `${this.fighter2.getName()} HP: ${hp2}\n`;
      output += `----------------------------------------------------------------\n`;
    }
  }
}

import { fighter } from './Fighter';

/**
 * Clase que representa a un Pokemon
 * @class Pokemon
 * @attribute {string} name Nombre del pokemon
 * @attribute {number} weight Peso del pokemon
 * @attribute {number} height Altura del pokemon
 * @attribute {number} attack Ataque del pokemon
 * @attribute {number} defense Defensa del pokemon
 * @attribute {number} hp Health Points del pokemon
 * @attribute {number} velocity Velocidad del pokemon
 * @attribute {string} type Tipo del pokemon
 * @attribute {string[]} catchingPhrases Frases que usa el pokemon
 */
export class Pokemon extends fighter {
  private velocity: number;
  private type: string;

  /**
   * Constructor
   * @param name {string} Nombre del pokemon
   * @param weight {number} Peso del pokemon
   * @param height {number} Altura del pokemon
   * @param attack {number} Ataque del pokemon
   * @param defense {number} Defensa del pokemon
   * @param hp {number} Health Points del pokemon
   * @param velocity {number} Velocidad del pokemon
   * @param type {string} Tipo del pokemon
   * @param catchingPhrases {string[]} Frases que usa el pokemon
   */
  constructor(name: string, weight: number, height: number,
      attack: number, defense: number, hp: number,
      velocity: number, type: 'fuego' | 'agua' | 'hierba' |
      'electrico' | 'normal', ...catchingPhrases: string[]) {
    super(name, weight, height, attack, defense, hp, ...catchingPhrases);
    this.velocity = velocity;
    this.type = type;
  }

  /**
   * Método que devuelve la velocidad del pokemon
   * @returns {number} Velocidad del pokemon
   */
  public getVelocity(): number {
    return this.velocity;
  }

  /**
   * Método que devuelve el tipo del pokemon
   * @returns {string} Tipo del pokemon
   */
  public getType(): string {
    return this.type;
  }

  /**
   * Método que devuelve la efectividad de un ataque
   * @param aType {string} Tipo del pokemon atacante
   * @param bType {string} Tipo del pokemon atacado
   * @returns {number} Efectividad del ataque
   */
  private effectiveness(aType: string, bType: string): number {
    if (aType === bType) {
      return 0.5;
    } else if (aType === 'fuego' && bType === 'hierba') {
      return 2;
    } else if (aType === 'fuego' && bType === 'agua') {
      return 0.5;
    } else if (aType === 'fuego' && bType === 'electrico') {
      return 1;
    } else if (aType === 'fuego' && bType === 'normal') {
      return 1;
    } else if (aType === 'agua' && bType === 'hierba') {
      return 0.5;
    } else if (aType === 'agua' && bType === 'electrico') {
      return 0.5;
    } else if (aType === 'agua' && bType === 'normal') {
      return 1;
    } else if (aType === 'hierba' && bType === 'electrico') {
      return 1;
    } else if (aType === 'hierba' && bType === 'normal') {
      return 2;
    } else if (aType === 'electrico' && bType === 'normal') {
      return 1;
    } else {
      const checkInverse: number = this.effectiveness(bType, aType);
      if (checkInverse === 2) {
        return 0.5;
      } else if (checkInverse === 0.5) {
        return 2;
      } else {
        return 1;
      }
    }
  }

  /**
   * Método que devuelve el daño de un ataque
   * @param enemy {fighter} Enemigo contra el que se ataca
   * @returns {number} Daño del ataque
   */
  public getDamage(enemy: fighter): number {
    if (enemy instanceof Pokemon) {
      return 50 * (this.attack / enemy.getDefense()) *
          this.effectiveness(this.type, enemy.type);
    } else {
      return 50 * (this.attack / enemy.getDefense());
    }
  }
}

/**
 * Clase que representa a un luchador
 * @class Fighter
 * @attribute {string} name Nombre del luchador
 * @attribute {number} weight Peso del luchador
 * @attribute {number} height Altura del luchador
 * @attribute {number} attack Ataque del luchador
 * @attribute {number} defense Defensa del luchador
 * @attribute {number} hp Health Points del luchador
 * @attribute {string[]} catchingPhrases Frases que usa el luchador
 */
export abstract class fighter {
  protected name: string;
  protected weight: number;
  protected height: number;
  protected attack: number;
  protected defense: number;
  protected hp: number;
  protected catchingPhrases: string[];

  /**
   * Constructor
   * @param name {string} Nombre del luchador
   * @param weight {number} Peso del luchador
   * @param height {number} Altura del luchador
   * @param attack {number} Ataque del luchador
   * @param defense {number} Defensa del luchador
   * @param hp {number} Health Points del luchador
   * @param catchingPhrases {string[]} Frases que usa el luchador
   */
  constructor(name: string, weight: number, height: number,
      attack: number, defense: number, hp: number,
      ...catchingPhrases: string[]) {
    this.name = name;
    this.weight = weight;
    this.height = height;
    this.attack = attack;
    this.defense = defense;
    this.hp = hp;
    this.catchingPhrases = catchingPhrases;
  }

  /**
   * Método que devuelve el nombre del luchador
   * @returns {string} Nombre del luchador
   */
  public getName(): string {
    return this.name;
  }

  /**
   * Método que devuelve el peso del luchador
   * @returns {number} Peso del luchador
   */
  public getWeight(): number {
    return this.weight;
  }

  /**
   * Método que devuelve la altura del luchador
   * @returns {number} Altura del luchador
   */
  public getHeight(): number {
    return this.height;
  }

  /**
   * Método que devuelve el ataque del luchador
   * @returns {number} Ataque del luchador
   */
  public getAttack(): number {
    return this.attack;
  }

  /**
   * Método que devuelve la defensa del luchador
   * @returns {number} Defensa del luchador
   */
  public getDefense(): number {
    return this.defense;
  }

  /**
   * Método que devuelve los puntos de vida del luchador
   * @returns {number} Health Points del luchador
   */
  public getHP(): number {
    return this.hp;
  }

  /**
   * Método que añade una frase al repertorio del luchador
   * @param phrase {string} Frase que usa el luchador
   */
  public addCatchingPhrase(phrase: string): void {
    this.catchingPhrases.push(phrase);
  }

  /**
   * Método que devuelve una frase del repertorio del luchador
   * @returns {string} Frase del repertorio del luchador
   */
  public getCatchingPhrase(): string {
    return this.catchingPhrases[Math.floor(Math.random() *
      this.catchingPhrases.length)];
  }

  /**
   * Método que calcula el daño que recibe un oponente
   * @param enemy {fighter} Oponente
   */
  abstract getDamage(enemy: fighter): number;
}

import { fighter } from './Fighter';

/**
 * Clase que representa a un Saiyan
 * @class Saiyan
 * @attribute {string} name Nombre del luchador
 * @attribute {number} weight Peso del luchador
 * @attribute {number} height Altura del luchador
 * @attribute {number} attack Ataque del luchador
 * @attribute {number} defense Defensa del luchador
 * @attribute {number} hp Health Points del luchador
 * @attribute {number} power Poder del luchador
 * @attribute {number} lvl Nivel del luchador
 * @attribute {string[]} catchingPhrases Frases que usa el luchador
 */
export class Saiyan extends fighter {
  private power: number;
  private lvl: number;

  /**
   * Constructor
   * @param name {string} Nombre del luchador
   * @param weight {number} Peso del luchador
   * @param height {number} Altura del luchador
   * @param attack {number} Ataque del luchador
   * @param defense {number} Defensa del luchador
   * @param hp {number} Health Points del luchador
   * @param power {number} Poder del luchador
   * @param lvl {number} Nivel del luchador
   * @param catchingPhrases {string[]} Frases que usa el luchador
   */
  constructor(name: string, weight: number, height: number,
      attack: number, defense: number, hp: number,
      power: number, lvl: number, ...catchingPhrases: string[]) {
    super(name, weight, height, attack, defense, hp, ...catchingPhrases);
    this.power = power;
    this.lvl = lvl;
  }

  /**
   * Método que devuelve el poder del luchador
   * @returns {number} Poder del luchador
   */
  public getPower(): number {
    return this.power;
  }

  /**
   * Método que devuelve el nivel del luchador
   * @returns {number} Nivel del luchador
   */
  public getLvl(): number {
    return this.lvl;
  }

  /**
   * Método que devuelve el daño de un ataque
   * @param enemy {fighter} Enemigo contra el que se ataca
   * @returns {number} Daño del ataque
   */
  public getDamage(enemy: fighter): number {
    return ((this.power / 10) * this.lvl * this.attack) / enemy.getDefense();
  }
}

import { fighter } from './Fighter';

/**
 * Clase que representa a un jugador de Sword Art Online
 * @class SwordArtOnline
 * @attribute {string} name Nombre del jugador
 * @attribute {number} weight Peso del jugador
 * @attribute {number} height Altura del jugador
 * @attribute {number} attack Ataque del jugador
 * @attribute {number} defense Defensa del jugador
 * @attribute {number} hp Health Points del jugador
 * @attribute {'Sniper' | 'Slayer' | 'Swordsman' | 'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker'} playerType Tipo de jugador
 * @attribute {number} weaponLevel Nivel de arma del jugador
 */
export class SwordArtOnline extends fighter {
  private playerType: 'Sniper' | 'Slayer' | 'Swordsman' |
    'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker';
  private weaponLevel: number;

  /**
   * Constructor
   * @param name {string} Nombre del jugador
   * @param weight {number} Peso del jugador
   * @param height {number} Altura del jugador
   * @param attack {number} Ataque del jugador
   * @param defense {number} Defensa del jugador
   * @param hp {number} Health Points del jugador
   * @param playerType {'Sniper' | 'Slayer' | 'Swordsman' | 'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker'} Tipo de jugador
   * @param weaponLevel {number} Nivel de arma del jugador
   * @param catchingPhrases {string[]} Frases que usa el jugador
   */
  constructor(name: string, weight: number, height: number,
      attack: number, defense: number, hp: number,
      playerType: 'Sniper' | 'Slayer' | 'Swordsman' |
        'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker',
      weaponLevel: number, ...catchingPhrases: string[]) {
    super(name, weight, height, attack, defense, hp, ...catchingPhrases);
    this.playerType = playerType;
    this.weaponLevel = weaponLevel;
  }

  /**
   * Método que devuelve el tipo de jugador
   * @returns {'Sniper' | 'Slayer' | 'Swordsman' | 'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker'} Tipo de jugador
   */
  public getPlayerType(): 'Sniper' | 'Slayer' | 'Swordsman' |
    'Ninja' | 'Samurai' | 'Monk' | 'Gunner' | 'Berserker' {
    return this.playerType;
  }

  /**
   * Método que devuelve el nivel de arma del jugador
   * @returns {number} Nivel de arma del jugador
   */
  public getWeaponLevel(): number {
    return this.weaponLevel;
  }

  /**
   * Método que devuelve el daño de un ataque
   * @param enemy {fighter} Enemigo contra el que se ataca
   * @returns {number} Daño del ataque
   */
  public getDamage(enemy: fighter): number {
    let attack: number = this.attack / 2;
    if (this.playerType === 'Sniper' || this.playerType === 'Slayer') {
      attack *= 2;
    }
    if (this.playerType === 'Swordsman' || this.playerType === 'Ninja' ||
        this.playerType === 'Samurai' || this.playerType === 'Monk') {
      attack *= 1.2;
    }
    if (this.playerType === 'Gunner' || this.playerType === 'Berserker') {
      attack *= 1.5;
    }
    return 50 * attack * this.weaponLevel / enemy.getDefense();
  }
}

import { fighter } from './Fighter';
import { Pokemon } from './Pokemon';
import { Saiyan } from './Saiyan';
import { SwordArtOnline } from './SAO';

/**
 * Clase que representa a un registro de un luchadores
 * @class Pokedex
 * @attribute {Pokemon[]} PokemonList Lista de pokemon
 * @attribute {Saiyan[]} SaiyanList Lista de saiyans
 * @attribute {SwordArtOnline[]} SAOList Lista de Sword Art Online Players
 */
export class Pokedex {
  PokemonList: Pokemon[];
  SAOList: SwordArtOnline[];
  SaiyanList: Saiyan[];

  /**
   * Constructor
   */
  constructor() {
    this.PokemonList = [];
    this.SAOList = [];
    this.SaiyanList = [];
  }

  /**
   * Método que permite añadir un luchador a su lista correspondiente
   * @param fighter {fighter} Luchador a añadir
   */
  public addFighter(fighter: fighter): void {
    if (fighter instanceof Pokemon) {
      this.PokemonList.push(fighter);
    } else if (fighter instanceof SwordArtOnline) {
      this.SAOList.push(fighter);
    } else {
      this.SaiyanList.push(fighter as Saiyan);
    }
  }

  /**
   * Método que devuelve un luchador a partir de su nombre
   * @param name {string} Nombre del luchador
   * @returns {fighter} Luchador
   */
  public getFighter(name: string): fighter | undefined {
    for (let pokemon of this.PokemonList) {
      if (pokemon.getName() === name) {
        return pokemon;
      }
    }
    for (let sao of this.SAOList) {
      if (sao.getName() === name) {
        return sao;
      }
    }
    for (let saiyan of this.SaiyanList) {
      if (saiyan.getName() === name) {
        return saiyan;
      }
    }
    return undefined;
  }

  /**
   * Método que devuelve una lista con todos los luchadores
   * @returns {fighter[]} Lista de luchadores
   */
  public getAllFighters(): fighter[] {
    let allFighters: fighter[] = [];
    allFighters = allFighters.concat(this.PokemonList);
    allFighters = allFighters.concat(this.SAOList);
    allFighters = allFighters.concat(this.SaiyanList);
    return allFighters;
  }
}

/**
 * Clase abstracta que representa contenido audiovisual
 * @class Audiovisual
 * @attribute {string} name Nombre del contenido audiovisual
 * @attribute {string} author Autor del contenido audiovisual
 * @attribute {number} year Año de publicación del contenido audiovisual
 * @attribute {string} genre Género del contenido audiovisual
 * @attribute {string} description Descripción del contenido audiovisual
 */
export abstract class Audiovisual {
  private name: string;
  private author: string;
  private year: number;
  private genre: string;
  private description: string;

  /**
   * Constructor
   * @param name {string} Nombre del contenido audiovisual
   * @param author {string} Autor del contenido audiovisual
   * @param year {number} Año de publicación del contenido audiovisual
   * @param genre {string} Género del contenido audiovisual
   * @param description {string} Descripción del contenido audiovisual
   */
  constructor(name: string, author: string, year: number, genre: string, description: string) {
    this.name = name;
    this.author = author;
    this.year = year;
    this.genre = genre;
    this.description = description;
  }

  /**
   * Método que devuelve el nombre del contenido audiovisual
   * @returns {string} Nombre del contenido audiovisual
   */
  public getName(): string {
    return this.name;
  }

  /**
   * Método que devuelve el autor del contenido audiovisual
   * @returns {string} Nombre del autor del contenido audiovisual
   */
  public getAuthor(): string {
    return this.author;
  }

  /**
   * Método que devuelve el año de publicación del contenido audiovisual
   * @returns {number} Año de publicación del contenido audiovisual
   */
  public getYear(): number {
    return this.year;
  }

  /**
   * Método que devuelve el género del contenido audiovisual
   * @returns {string} Género del contenido audiovisual
   */
  public getGenre(): string {
    return this.genre;
  }

  /**
   * Método que devuelve la descripción del contenido audiovisual
   * @returns {string} Descripción del contenido audiovisual
   */
  public getDescription(): string {
    return this.description;
  }
}

/**
 * Clase que representa un documental
 * @class Documental
 * @extends Audiovisual
 * @attribute {number} duration Duración del documental
 */
export class Documental extends Audiovisual {
  private duration: number;

  /**
   * Constructor
   * @param name {string} Nombre del documental
   * @param author {string} Autor del documental
   * @param year {number} Año de publicación del documental
   * @param genre {string} Género del documental
   * @param description {string} Descripción del documental
   * @param duration {number} Duración del documental
   */
  constructor(name: string, author: string, year: number, genre: string, description: string, duration: number) {
    super(name, author, year, genre, description);
    this.duration = duration;
  }

  /**
   * Método que devuelve la duración del documental
   * @returns {number} Duración del documental
   */
  public getDuration(): number {
    return this.duration;
  }
}

/**
 * Clase que representa una película
 * @class Film
 * @extends Audiovisual
 * @attribute {number} duration Duración de la película
 * @attribute {number} stars Número de estrellas de la película
 */
export class Film extends Audiovisual {
  private duration: number;
  private stars: number;

  /**
   * Constructor
   * @param name {string} Nombre de la película
   * @param author {string} Autor de la película
   * @param year {number} Año de publicación de la película
   * @param genre {string} Género de la película
   * @param description {string} Descripción de la película
   * @param duration {number} Duración de la película
   * @param stars {number} Número de estrellas de la película
   */
  constructor(name: string, author: string, year: number, genre: string, description: string, duration: number, stars: number) {
    super(name, author, year, genre, description);
    this.duration = duration;
    this.stars = stars;
  }

  /**
   * Método que devuelve la duración de la película
   * @returns {number} Duración de la película
   */
  public getDuration(): number {
    return this.duration;
  }

  /**
   * Método que devuelve el número de estrellas de la película
   * @returns {number} Número de estrellas de la película
   */
  public getStars(): number {
    return this.stars;
  }
}

/**
 * Clase que representa una serie
 * @class Serie
 * @extends Audiovisual
 * @attribute {number} seasons Número de temporadas de la serie
 * @attribute {number} episodes Número de episodios de la serie
 */
export class Serie extends Audiovisual {
  private seasons: number;
  private episodes: number;

  /**
   * Constructor
   * @param name {string} Nombre de la serie
   * @param author {string} Autor de la serie
   * @param year {number} Año de publicación de la serie
   * @param genre {string} Género de la serie
   * @param description {string} Descripción de la serie
   * @param seasons {number} Número de temporadas de la serie
   * @param episodes {number} Número de episodios de la serie
   */
  constructor(name: string, author: string, year: number, genre: string, description: string, seasons: number, episodes: number) {
    super(name, author, year, genre, description);
    this.seasons = seasons;
    this.episodes = episodes;
  }

  /**
   * Método que devuelve el número de temporadas de la serie
   * @returns {number} Número de temporadas de la serie
   */
  public getSeasons(): number {
    return this.seasons;
  }

  /**
   * Método que devuelve el número de episodios de la serie
   * @returns {number} Número de episodios de la serie
   */
  public getEpisodes(): number {
    return this.episodes;
  }
}

import { BasicStreamableCollection } from './BasicStreamableCollection';
import { Documental } from './audiovisual';

/**
 * Clase que representa una colección de documentales
 * @class DocumentalsCollection
 * @extends BasicStreamableCollection
 */
export class DocumentalsCollection extends BasicStreamableCollection<Documental> {
  /**
   * Método que busca un documental en la colección por su nombre
   * @param name {string} Nombre del documental
   * @returns {Documental[]} Colección de documentales
   */
  public searchByName(name: string): Documental[] {
    return this.items.filter((item) => item.getName() === name);
  }

  /**
   * Método que busca un documental en la colección por su año
   * @param year {number} Año del documental
   * @returns {Documental[]} Colección de documentales
   */
  public searchByYear(year: number): Documental[] {
    return this.items.filter((item) => item.getYear() === year);
  }

  /**
