export class Cifrado {
  private _texto: string;
  private _clave: string;
  private _alfabeto: string;

  constructor(texto: string, clave: string, alfabeto?: string) {
    this._texto = texto;
    this._clave = clave;
    this._alfabeto = alfabeto || 'abcdefghijklmnopqrstuvwxyz';
  }

  public cifrar(): string {
    let textoCifrado: string = '';
    let clave: number = 0;

    for (let i = 0; i < this._texto.length; i++) {
      const posicion: number = this._alfabeto.indexOf(this._texto[i]);
      if (posicion !== -1) {
        clave = this._alfabeto.indexOf(this._clave[i % this._clave.length]);
        textoCifrado += this._alfabeto[(posicion + clave) %
            this._alfabeto.length];
      } else {
        textoCifrado += this._texto[i];
      }
    }

    return textoCifrado;
  }

  public descifrar(): string {
    let textoDescifrado: string = '';
    let clave: number = 0;

    for (let i = 0; i < this._texto.length; i++) {
      const posicion: number = this._alfabeto.indexOf(this._texto[i]);
      if (posicion !== -1) {
        clave = this._alfabeto.indexOf(this._clave[i % this._clave.length]);
        textoDescifrado += this._alfabeto[(posicion - clave +
            this._alfabeto.length) % this._alfabeto.length];
      } else {
        textoDescifrado += this._texto[i];
      }
    }

    return textoDescifrado;
  }
}

import { SearchableCollection } from './SearchableCollection';

/**
 * NumericSearchableCollection es una clase que define una colección de
 * números que pueden ser buscados.
 * @class NumericSearchableCollection
 * @extends {SearchableCollection<number>}
 * @implements {Searchable<number>}
 * @implements {Collectable<number>}
 */
export class NumericSearchableCollection <T extends number>
  extends SearchableCollection <T> {
  /**
   * Método que realiza una búsqueda en la colección y devuelve todos los
   * elementos que coincidan con el término de búsqueda.
   * @param searchTerm Elemento a buscar.
   * @returns {number[]} Elementos que coinciden con el término de búsqueda.
   * @returns {undefined} Si no hay elementos que coincidan con el término de
   */
  search(searchTerm: T): T[] | undefined {
    return this.items.filter((item) => item === searchTerm);
  }
}

import { Collectable } from './Collectable';
import { Searchable } from './Searchable';

/**
 * SearchableCollection es una clase abstracta que define una colección de
 * elementos que pueden ser buscados.
 * @abstract
 * @class SearchableCollection
 * @implements {Collectable}
 * @implements {Searchable}
 */
export abstract class SearchableCollection <T> implements Collectable<T>,
    Searchable<T> {
  protected items: T[] = [];

  /**
   * Añade un item de tipo T a la colección.
   * @param item Item a añadir.
   */
  addItem(item: T): void {
    this.items.push(item);
  }

  /**
   * Devuelve los elementos de la colección.
   * @returns {T[]} Elementos de la colección.
   */
  getItems(): T[] {
    return this.items;
  }

  /**
   * Elimina un elemento de la colección por su índice.
   * @param index Índice del elemento a eliminar.
   */
  removeItem(index: number): void {
    this.items.splice(index, 1);
  }

  /**
   * Devuelve el número de elementos de la colección.
   * @returns {number} Número de elementos de la colección.
   */
  getNumberOfItems(): number {
    return this.items.length;
  }

  /**
   * Método abstracto que realiza una búsqueda en la colección.
   * @param searchTerm Elemento a buscar.
   * @returns {T[]} Elementos que coinciden con el término de búsqueda.
   * @returns {undefined} Si no hay elementos que coincidan con el término de
   * búsqueda.
   */
  abstract search(searchTerm: T): T[] | undefined;
}

import { SearchableCollection } from './SearchableCollection';

/**
 * StringSearchableCollection es una clase que define una colección de
 * cadenas que pueden ser buscadas.
 * @class StringSearchableCollection
 * @extends {SearchableCollection<string>}
 * @implements {Searchable<string>}
 * @implements {Collectable<string>}
 */
export class StringSearchableCollection <T extends string>
  extends SearchableCollection <T> {
  /**
   * Método que realiza una búsqueda en la colección y devuelve todos los
   * string que contengan la subcadena de búsqueda.
   * @param searchTerm Subcadena a buscar.
   * @returns {string[]} Elementos que coinciden con el término de búsqueda.
   */
  search(searchTerm: T): T[] | undefined {
    return this.items.filter((item) => item.includes(searchTerm));
  }
}
